export const getServiceBySlug = (slug: string): ServiceData | undefined => {
  return servicesData.find((service) => service.slug === slug);
};

export const serviceNavItems = servicesData.map((service) => ({
  name: service.title,
  slug: service.slug,
  href: `/services/${service.slug}`,
}));

